@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

}
<header>
  <h2>NgVGDB</h2>
  <div class="search-input">
    <div>
        <input type="text" placeholder="Search 500,000+ games" />
